import { ValidatedForm } from 'uniforms';
import BaseForm from './BaseForm';
const Validated = (parent) => {
    class _ extends ValidatedForm.Validated(parent) {
    }
    _.Validated = Validated;
    return _;
};
export default Validated(BaseForm);
