import { connectField, joinName } from 'uniforms';
import AutoField from './AutoField';
import ListDelField from './ListDelField';
const ListItem = props => (React.createElement("div", { className: "item" },
    React.createElement(ListDelField, { className: "top aligned", name: props.name }),
    React.createElement("div", { className: "middle aligned content", style: { width: '100%' } }, props.children ? (Children.map(props.children, child => React.cloneElement(child, {
        name: joinName(props.name, child.props.name),
        label: null,
        style: Object.assign({ margin: 0 }, child.props.style),
    }))) : (React.createElement(AutoField, Object.assign({}, props, { style: { margin: 0 } }))))));
